<?phpnamespace VS\Controllers;//// Базовый класс контроллера.//abstract class BaseController{    protected $params;	// Генерация внешнего шаблона	protected abstract function render($base_template);		// Функция, отрабатывающая до основного метода	protected abstract function before();		public function request($action, $base_template, $params)	{        $this->params = $params;		$this->before();		$this->$action();		$this->render($base_template);	}		//	// Запрос произведен методом GET?	//	protected function isGet()	{		return $_SERVER['REQUEST_METHOD'] == 'GET';	}	//	// Запрос произведен методом POST?	//	protected function isPost()	{		return $_SERVER['REQUEST_METHOD'] == 'POST';	}	//	// Генерация HTML шаблона в строку.	//	protected function template($fileName, $vars = array())	{		// Установка переменных для шаблона.		foreach ($vars as $k => $v)		{			$$k = $v;		}		// Генерация HTML в строку.		ob_start();		include "$fileName";		return ob_get_clean();		}			// Если вызвали метод, которого нет - завершаем работу	public function __call($name, $params){        die('Произошла какая-то печалька. Запрос не может быть обработан(');	}}