<?phpnamespace VS\Controllers;//// Базовый контроллер сайта.//abstract class SiteController extends BaseController{	protected $title; // заголовок страницы    protected $menu; // массив элементов верхнего меню страницы	protected $content; // содержимое страницы    protected $error_msg; // сообщение об ошибке	protected function before()	{		$this->title = 'Simple messenger';        $this->menu = array('Главная' => 'index.php',                            'Отправка сообщения' => 'index.php?act=ShowSendingForm',                            'Последние сообщения' => 'index.php?act=ShowLastMessages',                            'Последние непрочитанные сообщения' => 'index.php?act=ShowLastUnreadMessages');		$this->content = '';        $this->error_msg = '';	}		//	// Генерация базового шаблона	//		public function render($base_template)	{		$vars = array('title' => $this->title, 'menu' => $this->menu, 'content' => $this->content, 'error_msg' => $this->error_msg);		$page = $this->template($base_template, $vars);		echo $page;	}	}