<?phprequire_once('startup.php');use VS\Controllers\MessagesController;function __autoload($className){    $classNameAsArray = explode('\\', $className);    if (count($classNameAsArray) > 0) {        //последний элемент массива содержит только имя класса        $className = $classNameAsArray[count($classNameAsArray)-1];    }    $dir = 'c';    if (mb_stripos($className, "model")){        $dir = 'm';    }    require_once($dir.'/'.$className.'.php');}//текущее действие$action = 'action';$action .= (isset($_REQUEST['act'])) ? $_REQUEST['act'] : 'Index';//контроллер//в рамках данного приложения используется всего один контроллер,//поэтому параметр "с" в запросах нигде не используется$controller = (isset($_REQUEST['c'])) ? $_REQUEST['c'] : 'Messages';//базовый шаблон для выводимой страницы//в рамках данного приложения используется всего один базовый шаблон$base_template = (isset($_REQUEST['t'])) ? $_REQUEST['t'] : 'main';$base_template = 'v/v_'.$base_template.'.php';switch ($controller){	case 'Messages':		$controller = new MessagesController();		break;    default:		$controller = new MessagesController();}$controller->request($action, $base_template, array());